mapper.select:
SELECT COUNT(*) COUNT
FROM USERS
WHERE USERS_ID = #{usersId}

SELECT SUBSTR(USERS_ID, 1, LENGTH(USERS_ID) - 3) || '***'
FROM USERS
WHERE USERS_NAME = #{userName} AND EMAIL = #{userMail}

mapper.update:
UPDATE USERS
SET USERS_PASSWORD = #{password}
WHERE USERS_ID = #{userId}

mapper.select:
select EMAIL 
FROM USERS
WHERE EMAIL = #{email}

select USERS_ID, USERS_PASSWORD 
FROM USERS
WHERE USERS_ID = #{usersId}
